mapper.select:
select
    emp_no as employeeNo, birth_date as birthDate, first_name as firstName, last_name as lastName, gender as gender, hire_date as hireDate
from employees


    AND first_name=#{ety.firstName}
    AND last_name=#{ety.lastName}

order by emp_no desc

     order by emp_no desc
     order by ${limit.sort} ${limit.dir}
     limit ${limit.start}, ${limit.limit}
